# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\n# @File    : __init__.py\n# @Software: PyCharm\n\nfrom fast_langdetect import detect_language, detect_multilingual\nfrom fast_langdetect import parse_sentence\n\n# Testing a variety of languages\nprint(parse_sentence("你好世界"))\nprint(parse_sentence("你好世界！Hello, world！Привет, мир！Hola mundo"))\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!Hola mundo"))\n\nprint(detect_language("hello world"))\nprint(detect_language("こんにちは世界"))\nprint(detect_language("안녕하세요 세계"))\nprint(detect_language("Bonjour le monde"))\nprint(detect_language("Hallo Welt"))\nprint(detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等"))\n