# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\nimport logging\nfrom fast_langdetect.ft_detect import detect_multilingual, detect_language\n\n# Configure logging\nlogging.basicConfig(level=logging.DEBUG)\n\n\ndef test_muti_detect():\n    result = detect_multilingual("hello world", low_memory=True)\n    assert result[0].get("lang") == "en", "ft_detect error"\n    logging.warning("Testing multilingual language detection. Deprecated functionality used.")\n\n\ndef test_detect_totally():\n    assert detect_language("hello world") == "en", "ft_detect error"\n    logging.warning("Testing language detection for 'hello world'. Deprecated functionality used.")\n    assert detect_language("你好世界") == "zh", "ft_detect error"\n    logging.warning("Testing language detection for '你好世界'. Deprecated functionality used.")\n    assert detect_language("こんにちは世界") == "ja", "ft_detect error"\n    logging.warning("Testing language detection for 'こんにちは世界'. Deprecated functionality used.")\n\n