# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\nimport pytest\nfrom fast_langdetect import detect\n\n\ndef test_muti_detect():\n    from fast_langdetect.ft_detect import detect_multilingual\n    result = detect_multilingual("hello world", low_memory=True)\n    assert result[0].get("lang") == "en", "ft_detect error"\n\n\ndef test_detect():\n    assert detect("hello world").get("lang") == "en", "ft_detect error"\n    assert detect("你好世界").get("lang") == "zh", "ft_detect error"\n    assert detect("こんにちは世界").get("lang") == "ja", "ft_detect error"\n    assert detect("안녕하세요 세계").get("lang") == "ko", "ft_detect error"\n    assert detect("Bonjour le monde").get("lang") == "fr", "ft_detect error"\n    assert detect("Hallo Welt").get("lang") == "de", "ft_detect error"\n    assert detect("Hola mundo").get("lang") == "es", "ft_detect error"\n    assert detect("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等").get("lang") == "zh", "ft_detect error"\n\n\ndef test_detect_totally():\n    assert detect("hello world").get("lang").upper() == "EN", "ft_detect error"\n    assert detect("你好世界").get("lang").upper() == "ZH", "ft_detect error"\n    assert detect("こんにちは世界").get("lang").upper() == "JA", "ft_detect error"\n    assert detect("안녕하세요 세계").get("lang").upper() == "KO", "ft_detect error"\n    assert detect("Bonjour le monde").get("lang").upper() == "FR", "ft_detect error"\n    assert detect("Hallo Welt").get("lang").upper() == "DE", "ft_detect error"\n    assert detect("Hola mundo").get("lang").upper() == "ES", "ft_detect error"\n    assert detect("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等").get("lang").upper() == "ZH", "ft_detect error"